import { Component, OnInit } from '@angular/core';
import { UserService } from '../../services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-profil',
  templateUrl: './profil.component.html',
})

export class ProfilComponent implements OnInit {

  profil: any = {};
  chargementEnCours: boolean = true;

  constructor(private userService: UserService, private router: Router) {   }

  ngOnInit(): void {
    this.goAccueil();

    this.getInfo();
    setTimeout(() => {
      this.chargementEnCours = false;
    }, 1000);

  }
  async getInfo() {
    this.profil = await this.userService. getUserInfo();
  }

  goAccueil() {
    const token = window.localStorage.getItem('tokenuser');
   if( token == null ){
    this.router.navigate(['/']);
   } else{
    this.router.navigate(['/profil']);
   }
  }
  goEdit(){
    const token = window.localStorage.getItem('tokenuser');
    if( token == null ){
     this.router.navigate(['/']);
    } else{
     this.router.navigate(['/profil']);
    }
  }
  goUpdatePic(){
    const token = window.localStorage.getItem('tokenuser');
    if( token == null ){
     this.router.navigate(['/']);
    } else{
     this.router.navigate(['/profil']);
    }
  }


}
<section class="bg-white space-top space-extra-bottom overflow-hidden">
  <div class="container">
    <div class="row">
      <div class="col-xl-6">
        <div class="about-card">
          <div class="about-card_img">
              <img class="w-100" [src]="profil.photo" alt="team image">
          </div>
        </div>
      </div>
      <div class="col-xl-6 ps-3 ps-xl-5">
        <div class="about-card"><div class="about-card_content mb-50">
            <h2 class="about-card_title">{{ profil.nom }} {{ profil.prenom }}</h2>
            <p class="about-card_text">Information personnelle et profesionnelle sur votre profil </p>
            <div class="team-info">
                <ul>
                    <li><b>Email:</b> <a>{{ profil.email }}</a></li>
                    <li><b> Contact:</b> <a>{{ profil.contact }}</a></li>
                    <li><b>Date de naissance:</b> <span>2 janvier 2006</span></li>
                    <li><b>Sexe:</b> <span>{{ profil.sexe }}</span></li>
                    <li><b>Heure de travail:</b> <span>{{ profil.debut }} heure à {{ profil.fin }} heure</span></li>

                </ul>
            </div>
            <div class="th-social">
              <a target="_blank" href="https://facebook.com/"><i class="fab fa-facebook-f"></i>
              </a><a target="_blank" href="https://twitter.com/"><i class="fab fa-twitter"></i></a>
              <a target="_blank" href="https://instagram.com/"><i class="fab fa-instagram"></i></a>
              <a target="_blank" href="https://linkedin.com/"><i class="fab fa-linkedin-in"></i></a>
              <a target="_blank" href="https://pinterest.com/"><i class="fab fa-pinterest-p"></i></a>
            </div>
        </div>
      </div>
      <div class="skill-card_wrapp">
        <a target="_blank" href="https://facebook.com/"><i class="fab fa-facebook-f"></i></a>
      </div>
    </div>
  </div>
</div>
