import { Component, OnInit } from '@angular/core';
import { ServicesService } from 'src/app/services/services.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-preferrence',
  templateUrl: './preferrence.component.html'
})
export class PreferrenceComponent implements OnInit {

  chargementEnCours: boolean = true;
  servicesList: any[] = [];

  donneesAffichees: any[] = [];
  taillePage: number = 9;
  pageActuelle: number = 1;
  pageTotal: number = 0;


  constructor(private servicesService: ServicesService, private router: Router) { }

  ngOnInit(): void {
    this.goAccueil();

    this.getServices();
    setTimeout(() => {
      this.chargementEnCours = false;
    }, 1000);

  }

  goAccueil() {
    const token = window.localStorage.getItem('tokenuser');
   if( token == null ){
    this.router.navigate(['/']);
   } else{
    this.router.navigate(['/preferrence']);
   }
  }



  async getServices() {
    
    this.servicesList = await this.servicesService.getPrefServices();
    this.donneesAffichees = this.getPageSlice(this.servicesList, this.pageActuelle);
    this.pageTotal = Math.ceil(this.servicesList.length / this.taillePage);

  }






  getPageSlice(data: any[], page: number): any[] {
    const startIndex = (page - 1) * this.taillePage;
    const endIndex = startIndex + this.taillePage;
    return data.slice(startIndex, endIndex);
  }

  setPage(page: number) {
    this.pageActuelle = page;
    this.donneesAffichees = this.getPageSlice(this.servicesList, this.pageActuelle);
  }

  getPageNumbers(): number[] {
    return Array(this.pageTotal).fill(0).map((_, index) => index + 1);
  }

  getFormattedPrice(prix: number) {
    const formattedPrice = prix.toLocaleString('fr-FR');
    return formattedPrice.replace(/\s/g, '.');
  }

}
<div *ngIf="chargementEnCours">
  <app-loader></app-loader>
</div>

<div *ngIf="!chargementEnCours">
  <app-header></app-header>

  <section class="overflow-hidden" id="service-sec">
      <div class="container">
          <div class="title-area text-center"><span class="sub-title style2">Nos services</span> <img
                  src="assets/img/theme-img/title_shape_1.svg" alt="">
              <h2 class="sec-title">Liste de vos préférences</h2>
          </div>
          <div class="row">
              <div class="col-xl-3">
                  <div class="widget widget_search">

                  </div>
                  <div class="widget widget_categories">
                      <h3 class="widget_title">Categories</h3>
                      <ul>
                          <li><a><img src="assets/img/theme-img/flower.svg" alt=""> Tous</a></li>

                      </ul>
                  </div>
              </div>
              <div class="col-xl-9">
                  <div class="row">
                      <div class="col-xl-4" *ngFor="let services of donneesAffichees">
                          <div class="price-card wow fadeInLeft">
                              <div class="price-card_content">
                                  <img [src]="services.image" alt="Veuillez-patienter" style="display: block; margin: auto; width: 60%; border-radius: 50%;">
                                  <hr class="style1">
                                  <div class="available-list">
                                      <ul>
                                          <li><img src="assets/img/theme-img/title_left.svg" alt=""> <b>{{ services.nom }}</b></li>
                                          <li> <i class="fa-light fa-wallet"></i> {{ getFormattedPrice(services.prix) }} Ar</li>
                                          <li><i class="fa-light fa-clock"></i> {{ services.duree }} min</li>
                                          <li><app-etoiles style="position: relative; left: 20px;" [score]="services.note" [id]="services._id" [type]=0 ></app-etoiles></li>
                                      </ul>
                                      <div class="price-btn"><a [routerLink]="['/rdv', services?._id]" class="th-btn">Voir<i
                                                  class="fa-solid fa-arrow-right ms-2"></i></a></div>
                                  </div>
                              </div>
                          </div>
                      </div>
                      <div class="th-pagination text-center pt-50">
                          <ul>
                              <li *ngFor="let pageNumber of getPageNumbers()">
                                  <a href="javascript:void(0);" (click)="setPage(pageNumber)">{{ pageNumber }}</a>
                              </li>
                          </ul>
                      </div>
                  </div>
          </div>

          </div>
      </div>
  </section>

  <app-footer></app-footer>
</div>
