import { Component, OnInit } from '@angular/core';
import { ServicesService } from 'src/app/services/services.service';
import { ActivatedRoute, Router } from '@angular/router';
import { RdvService } from 'src/app/services/rdv.service';
import { PreferenceService } from 'src/app/services/preference.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-rdv',
  templateUrl: './rdv.component.html'
})
export class RdvComponent implements OnInit {

  chargementEnCours: boolean = true;
  services: any;
  searchElements = { keyWords: '', categorie: ''};

  preferenceService = 0;
  employeePreferee: any[] = [];

  dateEtHeureRdv: string = "";

  constructor(private route: ActivatedRoute, private router: Router, private servicesService: ServicesService, private rdvService: RdvService, private prefService:PreferenceService)
  { }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id')?.toString();
    this.getServiceByID(id);
    setTimeout(() => {
      this.chargementEnCours = false;
    }, 600);

  }

  afficherAlerte(feedBackRdv: any) {
    const rdv = feedBackRdv.rdv;

    if( rdv.date === undefined ){
      const htmlMessage = '<div style="text-align: left; padding: 2px;">'+
      '<p style="color: tomato;">'+ feedBackRdv.message + '</p>' +
      '</div>';

      Swal.fire({
        title: 'Oops',
        html: htmlMessage,
        icon: 'question',
        width: '600px',
        confirmButtonText: 'Ok',
        confirmButtonColor: '#FFB0B0',

      }).then((result) => {});
    }

    else{
      const htmlMessage = '<div style="text-align: left; padding: 2px;">'+
      '<p><b>Service</b> : '+ rdv.service.nom + '</p>' +
      '<p><b>Date et heure</b> : '+ this.getDateFromString(rdv.heure_debut) + ' à '+ this.getTimeFromString(rdv.heure_debut)+' - '+this.getTimeFromString(rdv.heure_fin)+ '</p>' +
      '<b>Avec</b> : '+rdv.employe.nom+' '+rdv.employe.prenom +'</p>' +
      '<p style="color: tomato;">'+ feedBackRdv.message + '</p>' +
      '</div>';

      Swal.fire({
        title: 'Veuillez-confirmer',
        html: htmlMessage,
        icon: 'question',
        width: '600px',
        showCancelButton: true,
        confirmButtonText: 'Valider',
        cancelButtonText: 'Annuler',
        confirmButtonColor: '#FFB0B0',

      }).then((result) => {
        if (result.isConfirmed) {
          this.fonctionOK(rdv);
        } else if (result.dismiss === Swal.DismissReason.cancel) {
        }
      });
    }
  }

  fonctionOK(rdv: any) {
    window.localStorage.setItem("lastrdv", JSON.stringify(rdv));
    this.router.navigate(['/paiement']);
  }

  async getServiceByID(id: string | undefined | null) {
      this.services = await this.servicesService.getServicesByID(id);
      this.employeePreferee = await this.services.employePreferee;
      this.preferenceService= await this.prefService.getPrefNote(id)
  }

  async prendreRdv() {
    const feedBackRdv = await this.rdvService.prendreRendezVous(this.services._id, this.dateEtHeureRdv);
    this.afficherAlerte(feedBackRdv);
  }

  getFormattedPrice(prix: number) {
    const formattedPrice = prix.toLocaleString('fr-FR');
    return formattedPrice.replace(/\s/g, '.');
  }

  setPreferenceService(nouveauScore: number): void {
    this.preferenceService = nouveauScore;
  }

  setPreferenceEmploye(nouveauScore: number, employeId: string): void {
    const nouveauxScores = this.employeePreferee.map(employe => {
      if (employe._id === employeId) {
        return { ...employe, note: nouveauScore };
      }
      return employe;
    });
    this.employeePreferee = nouveauxScores;
  }

  getDateFromString(dateObj: string){
    return dateObj.split('T')[0];
  }

  getTimeFromString(dateObj: string){
    return dateObj.split('T')[1].substring(0, 5);
  }

  async getPreferred(id: string | undefined | null) {
    this.services = await this.servicesService.getServicesByID(id);
    this.employeePreferee = await this.services.employePreferee;
  }

}
<div *ngIf="chargementEnCours">
    <app-loader></app-loader>
</div>

<div *ngIf="!chargementEnCours">
    <app-header></app-header>
    <br>
    <section class="overflow-hidden" id="service-sec">
        <div class="container">
            <div class="row">
                <div class="col-xl-4">
                    <div>
                    <img [src] = "services.image" alt="Chargement..."  style="object-fit: cover; object-position: center; height: 200px; display: block; margin: auto;">
                    </div>
                    <br>
                    <div style="text-align: center;">
                        <h5 >{{ services.nom }}</h5>
                        <div class="header-links">
                            <ul>
                                <li><i class="far fa-wallet"></i><a >{{ getFormattedPrice(services.prix) }} Ar</a></li>
                                <li><i class="far fa-clock"></i><a>{{ services.duree }} Min</a></li>
                            </ul>
                            <ul>
                                <li><b>Préférences</b> : <app-etoiles [score]="preferenceService" [type]=0 [id]="services._id"></app-etoiles></li>
                            </ul>
                        </div>
                    </div><br>
                    <p>{{ services.description }}</p>
                </div>

                <div class="col-xl-3">
                    <div class="row">
                        <img src="assets/img/illustration/rdv.svg" style="width: 60%; display: block; margin: auto;">
                        <p style="text-align: center;"><b>Prendre rendez-vous</b></p><br>
                        <div class="form-group col-md-12">
                            <label for="daterdv" style="color: grey;">Date et heure</label>
                            <input type="datetime-local" [(ngModel)]="dateEtHeureRdv" class="form-control" name="daterdv" id="daterdv" placeholder="daterdv">
                        </div>

                        <div class="form-btn col-12">
                            <button class="th-btn fw-btn" (click)="prendreRdv()">Prendre<i class="fa-solid fa-arrow-right ms-2"></i>
                            </button>
                        </div>
                    </div>
                </div>

            <div class="col-xl-5">
                    <div class="row">
                        <h5>Employés du service</h5>
                        <p>Vous pouvez ajouter des préférences sur les employés de ce service.</p>
                        <div class="col-xl-4" *ngFor="let employe of employeePreferee" >
                            <img [src]="employe.photo" style="border-radius: 50%; width: 60%; display: block; margin: 0 auto;">
                            <p style="text-align: center;">{{ employe.nom }} {{ employe.prenom }}</p>
                            <app-etoiles style="position: relative; left: 20px;" [score]="employe.note" [type]=1 [id]="employe._id" (scoreChange)="setPreferenceEmploye($event, employe._id)"></app-etoiles>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    </section>
    <br>
    <app-footer></app-footer>
</div>
