import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ConfirmationService } from '../../services/emailverify.service';

@Component({
  selector: 'app-confirmation',
  templateUrl: './emailverify.component.html'
})
export class EmailVerifyComponent implements OnInit {
  feedBack :any;
  valideUrl=false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private confirmationService: ConfirmationService
  ) { }

  ngOnInit(): void {
    const userId = this.route.snapshot.params.id;
    const token = this.route.snapshot.params.token;

    this.confirmationService.verifyEmail(userId, token).subscribe(
      response => {
        this.feedBack =  response;
        this.valideUrl=true
      },
      error => {
        console.error('Error:', error.error);
        this.feedBack=error.error;
        //this.router.navigate(['/']);

      }

    );
  }

  goToLogin() {
    this.router.navigate(['/']);
  }

}

<div class="">
  <div class="container">
      <div class="row">
          <div class="col-xl-12">
              <h4>Confirmation d'inscription</h4>
              <p>Veuillez-vous connecter pour bénéficier de nos services en ligne</p>

            <div class="feedback">
                <div *ngIf="valideUrl; else notFound">
                  <p style="color: green;">Email verified successfully <i class="fa-sharp fa-regular fa-warning"></i> </p>
                  <button class="green_btn" (click)="goToLogin()">Se connecter</button>
                </div>

            </div>

            <ng-template #notFound>
              <p style="color: tomato;">404 not found <i class="fa-sharp fa-regular fa-warning"></i> </p>
            </ng-template>


          </div>
        </div>
  </div>
</div>
